import React, { useState } from 'react';
import style from './SearchBar.module.css'

export default function SearchBar({onSearch}) {
  // ac치 va tu c칩digo
  const [city, setCity] = useState("");
  return (
    <form onSubmit={(e) => {
      e.preventDefault();
      onSearch(city);
    }}>
      <input
       type="text"
       placeholder="Ciudad..."
       value={city}/>
    </form>
  )
};
